import React, { useState, useEffect, createContext, useContext } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import './App.css';
import Login from './components/Login';
import Signup from './components/Signup';
import Footer from './components/Hocvien/Footer';
import Sidebar from './components/Hocvien/Sidebar';
import Header from './components/Hocvien/Header';
import Home from './components/Hocvien/Home';
import MyCourse from './components/Hocvien/MyCourse';
import LearningProgress from './components/Hocvien/LearningProgress';
import Article from './components/Hocvien/Article';
import CourseReactJS from './components/Hocvien/Course Information/CourseReactJS';
import Learn1 from './components/Hocvien/Course Information/Study Course/Learn1';
import UserManagement from './components/Admin/User/UserManagement';
import CourseManagement from './components/Admin/Course/CourseManagement';
import Headera from './components/Admin/Headera';
import Sidebara from './components/Admin/Sidebara';
import AddCourseModal from './components/Admin/Course/Function/AddCourse';
import EditCourse from './components/Admin/Course/Function/EditCourse';
import CourseForm from './components/giangvien/CourseForm';
import FeedbackList from './components/giangvien/FeedbackList';
import CourseTable from './components/giangvien/CourseTable';
import CourseInfo from './components/giangvien/CourseInfo';
import Sidebars from './components/giangvien/Sidebar';
import Headers from './components/giangvien/Header';
import Homeg from './components/giangvien/Homeg';
import Feature from './components/giangvien/Feature';

// Auth Context
const AuthContext = createContext();

const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check for existing token on mount
    const token = localStorage.getItem('token');
    if (token) {
      // Verify token with API
      fetch('http://your-api-url/verify-token', {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(res => res.json())
        .then(data => {
          if (data.user) {
            setUser(data.user);
          }
          setLoading(false);
        })
        .catch(() => {
          localStorage.removeItem('token');
          setLoading(false);
        });
    } else {
      setLoading(false);
    }
  }, []);

  const login = async (credentials) => {
    try {
      const response = await fetch('http://your-api-url/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(credentials)
      });
      const data = await response.json();
      if (data.token) {
        localStorage.setItem('token', data.token);
        setUser(data.user);
        return data;
      }
      throw new Error(data.message);
    } catch (error) {
      throw error;
    }
  };

  const signup = async (userData) => {
    try {
      const response = await fetch('http://your-api-url/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userData)
      });
      const data = await response.json();
      if (data.token) {
        localStorage.setItem('token', data.token);
        setUser(data.user);
        return data;
      }
      throw new Error(data.message);
    } catch (error) {
      throw error;
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, login, signup, logout, loading }}>
      {children}
    </AuthContext.Provider>
  );
};

// Protected Route Component
const ProtectedRoute = ({ children, allowedRoles }) => {
  const { user, loading } = useContext(AuthContext);
  
  if (loading) return <div>Loading...</div>;
  
  if (!user) return <Navigate to="/login" />;
  
  if (allowedRoles && !allowedRoles.includes(user.role)) {
    return <Navigate to="/" />;
  }
  
  return children;
};

// Main App Component
function App() {
  const [courses, setCourses] = useState([
    {
      id: 1,
      courseName: 'Java Core',
      instructor: 'Nguyễn Văn A',
      lessons: 20,
      description: 'Cung cấp kiến thức cơ bản về OOP, design pattern',
      startDate: '2023-01-01',
      endDate: '2023-03-31',
      status: 'Hoạt động',
      coverImage: null,
      objectives: [],
      lectures: [],
    },
    {
      id: 2,
      courseName: 'ReactJS',
      instructor: 'Trần Thị B',
      lessons: 15,
      description: 'Học cách xây dựng ứng dụng với ReactJS',
      startDate: '2023-02-01',
      endDate: '2023-04-30',
      status: 'Không hoạt động',
      coverImage: null,
      objectives: [],
      lectures: [],
    },
  ]);

  return (
    <Router>
      <AuthProvider>
        <Routes>
          {/* Public Routes */}
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<Signup />} />

          {/* Student Routes */}
          <Route
            path="/home"
            element={
              <ProtectedRoute allowedRoles={['student']}>
                <div className="app-wrapper">
                  <div className="container">
                    <Sidebar />
                    <div className="content">
                      <Header title="Trang chủ" isSearch={true} />
                      <Home />
                    </div>
                  </div>
                  <Footer />
                </div>
              </ProtectedRoute>
            }
          />
          <Route
            path="/my-course"
            element={
              <ProtectedRoute allowedRoles={['student']}>
                <div className="app-wrapper">
                  <div className="container">
                    <Sidebar />
                    <div className="content">
                      <Header title="Khóa học của tôi" />
                      <MyCourse />
                    </div>
                  </div>
                  <Footer />
                </div>
              </ProtectedRoute>
            }
          />
          <Route
            path="/progress"
            element={
              <ProtectedRoute allowedRoles={['student']}>
                <div className="app-wrapper">
                  <div className="container">
                    <Sidebar />
                    <div className="content">
                      <Header title="Tiến độ học tập" />
                      <LearningProgress />
                    </div>
                  </div>
                  <Footer />
                </div>
              </ProtectedRoute>
            }
          />
          <Route
            path="/article"
            element={
              <ProtectedRoute allowedRoles={['student']}>
                <div className="app-wrapper">
                  <div className="container">
                    <Sidebar />
                    <div className="content">
                      <Header title="Bài viết" />
                      <Article />
                    </div>
                  </div>
                  <Footer />
                </div>
              </ProtectedRoute>
            }
          />
    <Route
  path="/course/:courseId"
  element={
    <ProtectedRoute allowedRoles={['student']}>
      <div className="app-wrapper">
        <Sidebar />
        <div className="content">
          <Header title="Thông tin khóa học" />
          <CourseReactJS courses={courses} />
        </div>
      </div>
    </ProtectedRoute>
  }
          />
          <Route
            path="/learn1"
            element={
              <ProtectedRoute allowedRoles={['student']}>
                <div className="app-wrapper">
                  <Sidebar />
                  <Header title="Học ReactJS" />
                  <Learn1 />
                </div>
              </ProtectedRoute>
            }
          />

          {/* Instructor Routes */}
          <Route
            path="/homeg"
            element={
              <ProtectedRoute allowedRoles={['instructor']}>
                <div className="app-wrapper">
                  <Sidebars />
                  <div className="content">
                    <Headers title="Trang chủ giảng viên" />
                    <Homeg />
                  </div>
                </div>
              </ProtectedRoute>
            }
          />
          <Route
            path="/courses"
            element={
              <ProtectedRoute allowedRoles={['instructor']}>
                <div className="app-wrapper">
                  <Sidebars />
                  <div className="content">
                    <Headers title="Danh sách khóa học" />
                    <CourseTable />
                  </div>
                </div>
              </ProtectedRoute>
            }
          />
          <Route
            path="/create-course"
            element={
              <ProtectedRoute allowedRoles={['instructor']}>
                <div className="app-wrapper">
                  <Sidebars />
                  <div className="content">
                    <Headers title="Tạo khóa học mới" />
                    <CourseForm isEdit={false} />
                  </div>
                </div>
              </ProtectedRoute>
            }
          />
          <Route
            path="/edit-course/:id"
            element={
              <ProtectedRoute allowedRoles={['instructor']}>
                <div className="app-wrapper">
                  <Sidebars />
                  <div className="content">
                    <Headers title="Chỉnh sửa khóa học" />
                    <Feature isEdit={true} />
                  </div>
                </div>
              </ProtectedRoute>
            }
          />
          <Route
            path="/course-info/:id"
            element={
              <ProtectedRoute allowedRoles={['instructor']}>
                <div className="app-wrapper">
                  <Sidebars />
                  <div className="content">
                    <Headers title="Thông tin khóa học" />
                    <CourseInfo />
                  </div>
                </div>
              </ProtectedRoute>
            }
          />
          <Route
            path="/feedback"
            element={
              <ProtectedRoute allowedRoles={['instructor']}>
                <div className="app-wrapper">
                  <Sidebars />
                  <div className="content">
                    <Headers title="Phản hồi từ học viên" />
                    <FeedbackList />
                  </div>
                </div>
              </ProtectedRoute>
            }
          />

          {/* Admin Routes */}
          <Route
            path="/user-management"
            element={
              <ProtectedRoute allowedRoles={['admin']}>
                <div className="container">
                  <Sidebara />
                  <Headera title="Quản lý người dùng" />
                  <UserManagement />
                </div>
              </ProtectedRoute>
            }
          />
          <Route
            path="/course-management"
            element={
              <ProtectedRoute allowedRoles={['admin']}>
                <div className="container">
                  <Sidebara />
                  <Headera title="Quản lý khóa học" />
                  <CourseManagement courses={courses} setCourses={setCourses} />
                </div>
              </ProtectedRoute>
            }
          />
          <Route
            path="/add-course"
            element={
              <ProtectedRoute allowedRoles={['admin']}>
                <div className="container">
                  <Sidebara />
                  <Headera title="Thêm khóa học" />
                  <AddCourseModal />
                </div>
              </ProtectedRoute>
            }
          />
          <Route
            path="/edit-course/:id"
            element={
              <ProtectedRoute allowedRoles={['admin']}>
                <div className="container">
                  <Sidebara />
                  <Headera title="Sửa khóa học" />
                  <EditCourse courses={courses} setCourses={setCourses} />
                </div>
              </ProtectedRoute>
            }
          />

          {/* Default Route */}
          <Route path="/" element={<Navigate to="/login" />} />
          <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
      </AuthProvider>
    </Router>
  );
}

export default App;
export { AuthContext };



/**         function AppContent() {
  const [courses, setCourses] = useState([]);
  const location = useLocation();  // Sử dụng useLocation để lấy đường dẫn hiện tại
  const showFooterPaths = ["/home", "/my-course", "/progress", "/article"];
  const shouldShowFooter = showFooterPaths.includes(location.pathname); // Kiểm tra xem footer có hiển thị không

  return (
    <div className="app-wrapper">
      <div className="container">
        <Sidebar />
        <div className="content">
          <Routes>
            <Route path="/home" element={<><Header title="Trang chủ" isSearch={true} /><Home /></>} />
            <Route path="/my-course" element={<><Header title="Khóa học của tôi" /><MyCourse /></>} />
