import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import './App.css';
import Login from './components/Login';
import Signup from './components/Signup';
import Footer from './components/Hocvien/Footer';
import Sidebar from './components/Hocvien/Sidebar';
import Header from './components/Hocvien/Header';
import Home from './components/Hocvien/Home';  {/* Đảm bảo Home được import đúng */}
import UserManagement from './components/Admin/User/UserManagement';
import CourseManagement from './components/Admin/Course/CourseManagement';
import Headera from './components/Admin/Headera';
import Sidebara from './components/Admin/Sidebara';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // ✅ Thêm dòng này vào

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={
            isAuthenticated ? (
              <Navigate to="/home" />
            ) : (
              <Login setIsAuthenticated={setIsAuthenticated} />
            )
          }
        />
        <Route
          path="/signup"
          element={<Signup setIsAuthenticated={setIsAuthenticated} />}
        />
        <Route
          path="/home"
          element={
            isAuthenticated ? (
              <div className="app-container">
                <Sidebar />
                <div className="app-content">
                  <Header />
                  <div className="main-content">
                    <Home />  {/* Gọi trang Home */}
                  </div>
                </div>
              </div>
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/user-management"
          element={
            isAuthenticated ? (
              <div className="app-container">
                <Sidebara />
                <div className="app-content">
                  <Headera />
                  <div className="main-content">
                    <UserManagement />
                  </div>
                </div>
              </div>
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/course-management"
          element={
            isAuthenticated ? (
              <div className="app-container">
                <Sidebara />
                <div className="app-content">
                  <Headera />
                  <div className="main-content">
                    <CourseManagement />
                  </div>
                </div>
              </div>
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/"
          element={<Navigate to={isAuthenticated ? "/home" : "/login"} />}
        />
      </Routes>
    </Router>
  );
}

